def read_last_lines(filepath, num_lines=10):
    if not os.path.exists(filepath):
        return ["[LOG] File not found."]
    with open(filepath, "r", encoding="utf-8", errors="ignore") as file:
        lines = file.readlines()
        return lines[-num_lines:] if lines else ["[LOG] No entries found."]

def test_log_reading():
    path = "logs/app.log"
    if not os.path.exists(path):
        print("[WARN] Log file doesn't exist.")
        return
    lines = read_last_lines(path, 5)
    print("Last 5 log lines:")
    for line in lines:
        print("  " + line.strip())
